import React, { useState } from "react";
import "./App.scss";
import { Fieldset } from "./Fieldset";

const rate = 2.64;

function App() {
  const [value, setValue] = useState(0);

  const onChangeInUSD = (value) => {
    setValue({ currency: "USD", value });
  };
  const onChangeInBYN = (value) => {
    setValue({ currency: "BYN", value });
  };

  return (
    <form className="app">
      <h2>Live currency converter</h2>
      <Fieldset
        legend="$, USD"
        currency="USD"
        value={USD}
        onChange={onChangeInUSD}
      />
      <Fieldset
        currency="BYN"
        legend="BYN"
        value={BYN}
        onChange={onChangeInBYN}
      />
    </form>
  );
}

