import React, { useState } from "react";
import "./App.scss";
import Fieldset from "./Fieldset";

function App() {
  const rate = 2.64;

  const [valueUSD, setValueUSD] = useState("");
  const [valueBYN, setValueBYN] = useState("");

  const toBYN = (USD) => {
    setValueBYN(Math.round(USD * rate * 100) / 100);
    setValueUSD(USD);
  };

  const toUSD = (BYN) => {
    setValueUSD(Math.round(USD * rate * 100) / 100);
    setValueBYN(BYN);
  };

  return (
    <form className="app">
      <h2>Live currency converter</h2>
      <Fieldset
        legend="$, USD"
        currency="USD"
        value={valueUSD}
        onChange={toBYN}
      />
      <Fieldset currency="BYN" legend="BYN" value={valueBYN} onChange={toUSD} />
    </form>
  );
}

export default App;
